macros.latex:
%% These definitions allow for a consistent look for %% mathematical structures. It is relatively safe to play %% around with variants if you desire other notation or looks.
%% General comments %% \ensuremath{} is a good idea
% Delimiters % tex4ht needs \protect before some delimiters, so notation moves to notation list OK % Too many protect's confuses the XML version, so we combine % \left and ight with delimiting info here % With an FCLA prefix, these are not meant for general use, or outside this file % angle brackets: \langle, angle % brackets: \lbrack, brack % single vertical: \lvert, vert % double vertical: \lVert, Vert % matching nulls: . % set brackets: \{, \} % % Following just need a \protect when used in macro, since not variable-sized (?) % \colon, \widehat % % Have not combined \left, ight with ( and ), doesn't seem necessary % Also left alone bare | in \submatrix

ewcommand*{\FCLAlangle}{\left\langle} 
ewcommand*{\FCLArangle}{ightangle} 
ewcommand*{\FCLAlbrack}{\left\lbrack} 
ewcommand*{\FCLArbrack}{ightbrack} 
ewcommand*{\FCLAlvert}{\left\lvert} 
ewcommand*{\FCLArvert}{ightvert} 
ewcommand*{\FCLAlVert}{\left\lVert} 
ewcommand*{\FCLArVert}{ightVert} 
ewcommand*{\FCLAlnull}{\left.} 
ewcommand*{\FCLArnull}{ight.} 
ewcommand*{\FCLAlsetbrack}{\left\{} 
ewcommand*{\FCLArsetbrack}{ight\}}
%%%%%%%%%%%%%%%%%%%%% % % Decorations and Conveniences % %%%%%%%%%%%%%%%%%%%%%
% 2006/09/17 Converted _, ^ to \sb, \sp for TeX4ht
% Definitions % An environment for definitions is given elsewhere. % Here we define a typeface to use when a term is defined % and route the term to the index % % Usage: \define{definition-term} % 
ewcommand*{\define}[1]{{ f #1}}
% In Computation Notes, we use a font % for the commands used to communicate with the device % % Usage: \computerfont{commands} % 
ewcommand*{\computerfont}[1]{ {	t#1} }
% Integers % % Usage: \Z 
ewcommand*{\Z}{\ensuremath{{\mathbb Z}}}
% Complex numbers, set of scalars % (Will obsolete \complex{
ull} construction eventually) % % Usage: \complexes 
ewcommand*{\complexes}{\ensuremath{{\mathbb C}}}
% n-space over complex field % % Usage: \complex{integer-dimension, or 
ull} % 
ewcommand*{\complex}[1]{\ensuremath{{\mathbb C}\sp{#1}}}
% n-space over real field % % Usage: eal{integer-dimension, or 
ull} % 
ewcommand*{eal}[1]{\ensuremath{{\mathbb R}\sp{#1}}}
% Complex number modulus % % Usage: \modulus{a+bi} % Presumes math mode % 
ewcommand*{\modulus}[1]{\ensuremath{\protect\FCLAlvert#1\protect\FCLArvert}}
% Complex number conjugation % % Usage: \conjugate{a+bi} % Presumes math mode % 
ewcommand*{\conjugate}[1]{\ensuremath{\overline{#1}}}
% Bold-face vectors (or use wiggles, arrows, whatever) % Subscripts go outside this construction % % Usage: \vect{a symbol to use as a vector} % Have to already be in math mode % 
ewcommand*{\vect}[1]{\ensuremath{{ f#1}}}
% Standardized look for the zero vector % % Usage: \zerovector % Presumes math mode % 
ewcommand*{\zerovector}{\ensuremath{\vect{0}}}
% Zero matrix % % Usage: \zeromatrix % For use in math mode, use a subscript when size is important % 
ewcommand*{\zeromatrix}{\ensuremath{{\cal O}}}
% An inner product without matrix notation % % Usage: \innerproduct{a-vector}{a-vector} % Presumes math mode 
ewcommand*{\innerproduct}[2]{\ensuremath{\protect\FCLAlangle#1,\,#2\protect\FCLArangle}}
% Norm of a vector % % Usage: 
orm{a-vector} % Presumes math mode 
ewcommand*{
orm}[1]{\ensuremath{\protect\FCLAlVert#1\protect\FCLArVert}}
% Determinant of a matrix (functional notation) % % Usage: \detname{A} % Presumes math mode 
ewcommand*{\detname}[1]{\ensuremath{\protect\det\protect\left(#1\protectight)}}
% Determinant of a matrix (vertical bars) % % Usage: \detbars{A} % Presumes math mode 
ewcommand*{\detbars}[1]{\ensuremath{\protect\FCLAlvert#1\protect\FCLArvert}}
% Dimension % % Usage: \dimension{vector-space-letter} % Presumes math mode % 
ewcommand*{\dimension}[1]{\ensuremath{\dim\protect\left(#1\protectight)}}
% Nullity % % Usage: 
ullity{matrix-or-lintrans-letter} % Presumes math mode % 
ewcommand*{
ullity}[1]{\ensuremath{n\protect\left(#1\protectight)}}
% Rank % % Usage: ank{matrix-or-lintrans-letter} % Presumes math mode % 
ewcommand*{ank}[1]{\ensuremath{r\protect\left(#1\protectight)}}
%%%%%%%%%%%%%%%%%%%%% % % Sets % %%%%%%%%%%%%%%%%%%%%%
% A convenience for simple sets % % Usage: \set{list of element} % Presumes math mode % 
ewcommand*{\set}[1]{\ensuremath{\protect\FCLAlsetbrack#1\protect\FCLArsetbrack}}
% Sets with vertical bar, "such that", sized for objects, not condition % % Usage: \setparts{objects}{condition} % Presumes math mode % %%
ewcommand*{\setparts}[2]{\ensuremath{\protect\FCLAlsetbrack #1\protect\mid#2\protect\FCLArsetbrack}} 
ewcommand*{\setparts}[2]{\ensuremath{\protect\FCLAlsetbrack\protect\FCLAlnull #1\protect\FCLArvert#2\protect\FCLArsetbrack}}
% Set Cardinality % % Usage: \card{a-set-letter} % Presumes math mode % 
ewcommand*{\card}[1]{\ensuremath{\protect\FCLAlvert#1\protect\FCLArvert}}
% Set Union % Use \cup
% Set Intersection % Use \cap
% Set Complement % % Usage: \setcomplement{a-set-letter} % Presumes math mode % 
ewcommand*{\setcomplement}[1]{\ensuremath{\overline{#1}}}
%%%%%%%%%%%%%%%%%%%%% % % Row Operations, Echelon Form % %%%%%%%%%%%%%%%%%%%%%
% Row operations on matrices % % Three commands to shorten up descriptions of gaussian elimination % % Usage: owopswap{row-i}{row-j} % Usage: owopmult{scalar}{row-i} % Usage: owopadd{scalar}{row-multiplied}{row-added-to} 
ewcommand*{owopswap}[2]{\ensuremath{R\sb{#1}\leftrightarrow R\sb{#2}}} 
ewcommand*{owopmult}[2]{\ensuremath{#1R\sb{#2}}} 
ewcommand*{owopadd}[3]{\ensuremath{#1R\sb{#2}+R\sb{#3}}}
% Mark leading 1's in echelon form % Just an fbox now, a *circle* eventually? % % Usage: \leading{a-1-usually} % 
ewcommand*{\leading}[1]{\ensuremath{ box{#1}}}
% Row-reduce arrow % % Usage: ref inbetween a matrix and its reduced row-echelon form % 
ewcommand*{ref}{\ensuremath{\xrightarrow{	ext{RREF}}}}
%%%%%%%%%%%%%%%%%%%%% % % 2-D Constructions (Lists, Vectors, Matrices) % %%%%%%%%%%%%%%%%%%%%%
% A list of scalars of generic length % % Usage: \scalarlist{scalar letter}{terminal subscript} % Have to already be in math mode %% 
ewcommand*{\scalarlist}[2] {\ensuremath{{#1}\sb{1},\,{#1}\sb{2},\,{#1}\sb{3},\,\ldots,\,{#1}\sb{#2}}}
% A column vector % % Usage: \colvector{list-delimited-by-\} % Presumes math mode % 
ewcommand*{\colvector}[1]{\ensuremath{ egin{bmatrix}#1\end{bmatrix}}}
% A generic vector with components % % Usage: \vectorcomponents{component-letter}{final-subscript} % Presumes math mode 
ewcommand*{\vectorcomponents}[2] {\ensuremath{\colvector{#1\sb{1}\#1\sb{2}\#1\sb{3}\\vdots\#1\sb{#2}}}}
% A list of vectors of generic length % % Usage: \vectorlist{vector letter}{terminal subscript} % Have to already be in math mode %% 
ewcommand*{\vectorlist}[2] {\ensuremath{\vect{#1}\sb{1},\,\vect{#1}\sb{2},\,\vect{#1}\sb{3},\,\ldots,\,\vect{#1}\sb{#2}}}
% Vector entries, entry-by-entry % % Entry i of vector v % vector-expession still needs \vect, etc. % % Usage: \vectorentry{vector-expression}{single-subscript} % 
ewcommand*{\vectorentry}[2]{\ensuremath{\protect\FCLAlbrack#1\protect\FCLArbrack\sb{#2}}}
% A generic linear combination % % Usage: \lincombo{scalar letter}{vector letter}{terminal subscript} % Have to already be in math mode % 
ewcommand*{\lincombo}[3] {\ensuremath{#1\sb{1}\vect{#2}\sb{1}+#1\sb{2}\vect{#2}\sb{2}+#1\sb{3}\vect{#2}\sb{3}+\cdots +#1\sb{#3}\vect{#2}\sb{#3}}}
% Matrix, column by column, as vectors % % Usage: \matrixcolumns{matrix letter}{terminal subscript} % Have to already be in math mode %% 
ewcommand*{\matrixcolumns}[2] {\ensuremath{\left\lbrack\vect{#1}\sb{1}|\vect{#1}\sb{2}|\vect{#1}\sb{3}|\ldots|\vect{#1}\sb{#2}ightbrack}}
%%%%%%%%%%%%%%%%%%%%% % % Systems of Equations % %%%%%%%%%%%%%%%%%%%%%
% In-line form of an augmented matrix for a system of equations % % Usage: \augmented{coefficient-matrix}{constant-vector} % Presumes in math mode % 
ewcommand*{\augmented}[2]{\ensuremath{\protect\FCLAlbrack\protect\FCLAlnull#1\,\protect\FCLArvert\,#2\protect\FCLArbrack}}
% Notation for a linear system before introducing matrix multiplication % % Usage: \linearsystem{coefficient-matrix}{constant-vector} % Presumes math mode % 
ewcommand*{\linearsystem}[2]{\ensuremath{{\mathcal L}{\mathcal S}\!\protect\left(#1,\,#2\protectight)}}
% Notation for a homogenous system before introducing matrix multiplication % Built on \linearsystem and \zerovector % % Usage: \homosystem{coefficient-matrix} % Presumes math mode % 
ewcommand*{\homosystem}[1]{\ensuremath{\linearsystem{#1}{\zerovector}}}
%%%%%%%%%%%%%%%%%%%%% % % Subspace Constructions % %%%%%%%%%%%%%%%%%%%%%
% Span of a set of vectors % % \span and \sp are used by TeX for other things % % Usage: \spn{set-of-vectors} % Presumes math mode, uses ams-math 	ext command % % Obsoleted 2006/01/05: Sp notation 
ewcommand*{\spn}[1]{\ensuremath{\protect\FCLAlangle#1\protect\FCLArangle}}
% Null space of a matrix % % A convenience for null spaces % % Usage: 
sp{A} % Presumes math mode % 
ewcommand*{
sp}[1]{\ensuremath{{\mathcal N}\!\protect\left(#1\protectight)}}
% Column Space of a matrix % % Usage: \csp{A} % Presumes math mode % 
ewcommand*{\csp}[1]{\ensuremath{\mathcal{C}\!\protect\left(#1\protectight)}}
% Row Space of a matrix % % Usage: sp{A} % Presumes math mode % 
ewcommand*{sp}[1]{\ensuremath{\mathcal{R}\!\protect\left(#1\protectight)}}
% Left Null space of a matrix % % A convenience for null spaces % % Usage: \lns{A} % Presumes math mode % 
ewcommand*{\lns}[1]{\ensuremath{\mathcal{L}\!\protect\left(#1\protectight)}}
% Range of a linear transformation % % TeX uses ange for something else % % Usage: ng{A} % Presumes math mode % 
ewcommand*{ng}[1]{\ensuremath{\mathcal{R}\!\protect\left(#1\protectight)}}
% Kernel of a linear transformation % % TeX uses \ker to do something different % % Usage: \ker{A} % Presumes math mode % 
ewcommand*{\krn}[1]{\ensuremath{\mathcal{K}\!\protect\left(#1\protectight)}}
%%%%%%%%%%%%%%%%%%%%% % % Matrices % %%%%%%%%%%%%%%%%%%%%%
% Matrix expressions, entry-by-entry % % Entry i,j of matrix A, usually when A is really an expression % We can change it here if we choose % % Usage: \matrixentry{matrix-expression}{paired-subscripts} % 
ewcommand*{\matrixentry}[2]{\ensuremath{\protect\FCLAlbrack#1\protect\FCLArbrack\sb{#2}}}
% Transpose of a matrix % % Usage: 	ranspose{A} % Presumes math mode % 
ewcommand*{	ranspose}[1]{\ensuremath{#1\sp{t}}}
% Adjoint of a matrix % % This macro is a convenience to call 	ranspose and \conjugate properly % It shouldn't need to be modified (or mathematical meanings will change) % % Usage: \adj{A} % Presumes math mode % 
ewcommand*{\adj}[1]{\ensuremath{	ranspose{\left(\conjugate{#1}ight)}}}
% This macro controls the symbol used for the adjoint % It can be edited to taste % % Usage: \adjoint{A} % Presumes math mode % 
ewcommand*{\adjoint}[1]{\ensuremath{#1\sp{\ast}}}
% Inverse of a matrix % % Usage: \inverse{A} % Presumes math mode % 
ewcommand*{\inverse}[1]{\ensuremath{#1\sp{-1}}}
% Similarity % % Usage: \similar{inner-matrix}{outer-invertible-matrix} % Presumes math mode % Rearranging this will not "fix" all desired changes throughout % 
ewcommand*{\similar}[2]{\ensuremath{\inverse{#2}#1#2}}
% Submatrix % % Usage: \submatrix{matrix-name}{delete-row}{delete-col} % Presumes math mode % 
ewcommand*{\submatrix}[3]{\ensuremath{#1\protect\left(#2|#3\protectight)}}
% Elementary Matrices % % Usage: \elemswap{subscript}{subscript} % Usage: \elemmult{scalar}{subscript} % Usage: \elemadd{scalar}{subscript-mult}{subscript-target} % Presumes math mode % 
ewcommand*{\elemswap}[2]{\ensuremath{E\sb{#1,#2}}} 
ewcommand*{\elemmult}[2]{\ensuremath{E\sb{#2}\protect\left(#1\protectight)}} 
ewcommand*{\elemadd}[3]{\ensuremath{E\sb{#2,#3}\protect\left(#1\protectight)}}
%%%%%%%%%%%%%%%%%%%%% % % Eigenvalues & Eigenspaces % %%%%%%%%%%%%%%%%%%%%%
% Characteristic polynomial % % Usage: \charpoly{matrix-letter}{variable-letter} % Presumes math mode % 
ewcommand*{\charpoly}[2]{\ensuremath{p\sb{#1}\left(#2ight)}}
% Eigenspace % % Usage: \eigenspace{matrix-letter}{eigenvalue-letter} % Presumes math mode % 
ewcommand*{\eigenspace}[2]{\ensuremath{{\mathcal E}\sb{#1}\left(#2ight)}}
% Generalized Eigenspace % % Usage: \geneigenspace{lin-trans-letter}{eigenvalue-letter} % Presumes math mode % 
ewcommand*{\geneigenspace}[2]{\ensuremath{{\mathcal G}\sb{#1}\protect\left(#2\protectight)}}
% Algebraic multiplicty % % Usage: \algmult{matrix-letter}{eigenvalue-letter} % Presumes math mode % 
ewcommand*{\algmult}[2]{\ensuremath{\alpha\sb{#1}\protect\left(#2\protectight)}}
% Geometric multiplicty % % Usage: \geomult{matrix-letter}{eigenvalue-letter} % Presumes math mode % 
ewcommand*{\geomult}[2]{\ensuremath{\gamma\sb{#1}\protect\left(#2\protectight)}}
% Index (of eigenvalue) % % Usage: \indx{matrix-letter}{eigenvalue-letter} % Presumes math mode % 
ewcommand*{\indx}[2]{\ensuremath{\iota\sb{#1}\protect\left(#2\protectight)}}
% Eigensystem % % Usage: \eigensystem{matrixletter}{eigenvalue}{list of basis vectors} % Presumes math mode and in align* environment % 
ewcommand*{\eigensystem}[3]{ \ensuremath{\lambda&=#2&\eigenspace{#1}{#2}&=\spn{\set{#3}}} }
%%%%%%%%%%%%%%%%%%%%% % % Linear Transformations % %%%%%%%%%%%%%%%%%%%%%
% Linear transformation definition % % Usage: \ltdefn{name-letter}{domain}{range} % Presumes math mode % % Through v 2.12: % 
ewcommand*{\ltdefn}[3]{\ensuremath{#1\protect\colon #2\mapsto#3}} % Now reserved for describing the action on an individual element 
ewcommand*{\ltdefn}[3]{\ensuremath{#1\protect\colon #2ightarrow#3}}
% Linear transformation output % % Usage: \lt{name-letter}{input} % Presumes math mode % 
ewcommand*{\lt}[2]{\ensuremath{#1\protect\left(#2\protectight)}}
% Linear transformation inverse % % Usage: \ltinverse{name-letter} % Presumes math mode % 
ewcommand*{\ltinverse}[1]{\ensuremath{#1\sp{-1}}}
% Linear transformation restriction % % Usage: estrict{name-letter}{subspace-letter} % Presumes math mode % 
ewcommand*{estrict}[2]{\ensuremath{{#1}|\sb{#2}}}
% Linear transformation preimage % % Usage: \preimage{name-letter}{codomain-element} % Presumes math mode % 
ewcommand*{\preimage}[2]{\ensuremath{#1\sp{-1}\protect\left(#2\protectight)}}
%%%%%%%%%%%%%%%%%%%%% % % Isomorphism and Representations % %%%%%%%%%%%%%%%%%%%%%
% Isomorphism symbol % % Usage: \isomorphic % Presumes math mode % 
ewcommand*{\isomorphic}{\ensuremath{\cong}}
% Vector representation function name % % Usage: \vectrepname{basis-letter} % Presumes math mode % 
ewcommand*{\vectrepname}[1]{\ensuremath{ho\sb{#1}}}
% Vector representation output % % Usage: \vectrep{basis-letter}{input} % Presumes math mode % 
ewcommand*{\vectrep}[2]{\ensuremath{\lt{\vectrepname{#1}}{#2}}}
% Vector representation inverse function name % (Added later, not used consistently) % % Usage: \vectrepinvname{basis-letter} % Presumes math mode % 
ewcommand*{\vectrepinvname}[1]{\ensuremath{\ltinverse{\vectrepname{#1}}}}
% Vector representation inverse output % % Usage: \vectrepinv{basis-letter}{input} % Presumes math mode % 
ewcommand*{\vectrepinv}[2]{\ensuremath{\lt{\ltinverse{\vectrepname{#1}}}{#2}}}
% Matrix representation % % Usage: \matrixrep{transformation-letter}{domain-basis-letter}{codomain-basis-letter} % Presumes math mode % 
ewcommand*{\matrixrep}[3]{\ensuremath{M\sp{#1}\sb{#2,#3}}}
% Matrix representation column-by-colum % % Usage: \matrixrepcolumns{transformation-letter}{codomain-basis-letter}{codomain-basis-vector-letter}{final-index} % Presumes math mode % 
ewcommand*{\matrixrepcolumns}[4]{ \ensuremath{\left\lbrack \left.\vectrep{#2}{\lt{#1}{\vect{#3}\sb{1}}}ight| \left.\vectrep{#2}{\lt{#1}{\vect{#3}\sb{2}}}ight| \left.\vectrep{#2}{\lt{#1}{\vect{#3}\sb{3}}}ight| \ldots \left|\vectrep{#2}{\lt{#1}{\vect{#3}\sb{#4}}}ight. ightbrack }}
% Change of basis % % Usage: \cbm{domain-basis-letter}{codomain-basis-letter} % Presumes math mode % 
ewcommand*{\cbm}[2]{\ensuremath{C\sb{#1,#2}}}
% Linear transformation composition % % Usage: \compose{function-name}{function-name} % Presumes math mode % 
ewcommand*{\compose}[2]{\ensuremath{{#1}\circ{#2}}}
% Vector space of linear transformations % % Usage: \vslt{domains}{codomains} % Presumes math mode % 
ewcommand*{\vslt}[2]{\ensuremath{{\mathcal LT}\left(#1,\,#2ight)}}
%%%%%%%%%%%%%%%%%%%%% % % Canonical Forms % %%%%%%%%%%%%%%%%%%%%%
% Jordan blocks % % Usage: \jordan{size}{diagonal-element} % Presumes math mode % 
ewcommand*{\jordan}[2]{\ensuremath{J\sb{#1}\protect\left(#2\protectight)}}
%%%%%%%%%%%%%%%%%%%%% % % Hadamard Matrices % %%%%%%%%%%%%%%%%%%%%%
% Contributed by Elizabeth Million, to support her Topics section, HP
% Usage: \hadamard{a-matrix}{a-matrix} % Presumes math mode 
ewcommand*{\hadamard}[2]{\ensuremath{#1\circ #2}}
% Hadamard identity matrix % % Usage: \hadamardidentity{paired-subscripts-size-of-matrix} 
ewcommand*{\hadamardidentity}[1]{\ensuremath{J_{#1}}}
% Hadamard inverse matrix % % Usage: \hadamardinverse{matrix-expression} 
ewcommand*{\hadamardinverse}[1]{\ensuremath{\protect\widehat{#1}}}
%%%%%%%%%%%%%%%%%%%%% % % Miscellaneous % %%%%%%%%%%%%%%%%%%%%%
% Direct sum % Conveniece for actual symbol % % Usage: \ds between a couple of subspaces % 
ewcommand*{\ds}{\ensuremath\oplus}
% Square Root of a Matrix % % Usage: \sr{a-matrix} % 
ewcommand*{\sr}[1]{\ensuremath#1\sp{1/2}}
% matrix trace % % Usage: 	race{matrix name} % Persumes in math mode 
ewcommand*{	race}[1]{\ensuremath{t\protect\left(#1\protectight)}}
% Indentation on pseudo-code % 2006/09/16 Changed fr0m 1.5em to 2em for Tex4ht % 2006/09/21 Changed back to 1.5em - too wide for text % % Usage: \codeindent{n} where n is the level of indentation desired % 
ewlength{\oneindent} \setlength{\oneindent}{1.5em} 
ewcommand*{\codeindent}[1]{
oindent\hspace*{#1\oneindent}}
